const transitionBase = cn('transition-colors duration-slideshow');

function arrowColorSelector(color: SlideshowLogoColor) {
  if (color === 'white') {
    return cn(transitionBase, 'fill-tertiary');
  }

  return cn(transitionBase, 'fill-grayscale-50');
}

function logoTextColorSelector(color: SlideshowLogoColor) {
  if (color === 'white') {
    return cn(transitionBase, 'fill-grayscale-50');
  }
  return cn(transitionBase, 'fill-primary');
}
